// Send the timestamp request to our timestamp server and save the response
//  $ curl --globoff -s -S -H Content-Type:application/timestamp-query -H Host:${HOST} --data-binary @request-sha256.tsq -o ts-output.tsr ${URL}

func TestParseRequest(t *testing.T) {
	req, err := ParseRequest(reqNonce)
	if err != nil {
		t.Fatal(err)
	}

	if !bytes.Equal(req.HashedMessage, hashedMessage) {
		t.Errorf("req.HashedMessage: got %x, want %x", req.HashedMessage, hashedMessage)
	}

	if req.HashAlgorithm != crypto.SHA256 {
		t.Errorf("req.HashAlgorithm: got %v, want %v", req.HashAlgorithm, crypto.SHA256)
	}

	if req.Certificates == false {
		t.Errorf("req.Certificates: got false, want true")
	}
}

func TestParseResponse(t *testing.T) {
	resp, err := ParseResponse(respNonce)
	if err != nil {
		t.Fatal(err)
	}

	if !bytes.Equal(resp.HashedMessage, hashedMessage) {
		t.Errorf("resp.HashedMessage: got %x, want %x", resp.HashedMessage, hashedMessage)
	}

	if resp.HashAlgorithm != crypto.SHA256 {
		t.Errorf("resp.HashAlgorithm: got %v, want %v", resp.HashAlgorithm, crypto.SHA256)
	}

	exTime := time.Date(2017, 4, 19, 6, 29, 53, 0, time.UTC)
	if !resp.Time.Equal(exTime) {
		t.Errorf("resp.Time: got %v, want %v", resp.Time, exTime)
	}

	if resp.Accuracy != time.Second {
		t.Errorf("resp.Accuracy: got %v, want %v", resp.Accuracy, time.Second)
	}
}

func TestMarshalRequest(t *testing.T) {
	req, err := ParseRequest(reqNoNonce)
	if err != nil {
		t.Fatal(err)
	}

	reqByes, err := req.Marshal()
	if err != nil {
		t.Fatal(err)
	}

	if !bytes.Equal(reqByes, reqNoNonce) {
		t.Error("Marshalled response bytes are not the same as parsed")
	}
}

func TestCreateRequest(t *testing.T) {
	msg := "Content to by timestamped"

	h := sha256.New()
	h.Write([]byte(msg))
	hashedMsg := h.Sum(nil)

	req, err := CreateRequest(strings.NewReader(msg), nil)
	if err != nil {
		t.Fatal(err)
	}

	if len(req) == 0 {
		t.Error("request contains no bytes")
	}

	reqCheck, err := ParseRequest(req)
	if err != nil {
		t.Fatal(err)
	}

	if !bytes.Equal(reqCheck.HashedMessage, hashedMsg) {
		t.Errorf("reqCheck.HashedMessage: got %x, want %x", reqCheck.HashedMessage, hashedMsg)
	}
}
